package practice;

public class A05UnaryOperators {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		boolean sunday = true;
		System.out.println("print " + a);
		a++;
		System.out.println("Print " + a);
		a--;
		System.out.println("Print " + a);
		a = -a;
		System.out.println("Print " + a);
		System.out.println("Print " + !sunday);
	}
}
